import React, { useState } from "react";
import { Input, Row, Col, Card, Typography, Button, Form } from "antd";

// card
const { Title } = Typography;

function App() {
  const [input, setinput] = useState("");
  console.log(input);
  const getWeather = () => {
    fetch('https://api.openweathermap.org/data/2.5/forecast?q={input}&appid=c2a66f2ea35002c3e18f831ebe3c1177')
  }

  return (
    <>
    <div className="App">
      <Row justify="center">
        <Col span={24}>
          <Card style={{ height: 100 }}>
            <Title level={1}>Weather App</Title>
          </Card>
        </Col>
      </Row>
      <br />
      <br />
      <Form>
        <Row justify="center" style={{ marginTop: 100 }}>
          <Col span={6}>
            <Input placeholder="Enter the city name......" size="large" onChange={(e)=>setinput(e.target.value)}/>
          </Col>
        </Row>
        <br />
        <br />
        <Row justify="center">
          <Col>
            <Button type="primary" htmlType="submit" onClick={getweather}>
              Search
            </Button>
          </Col>
        </Row>
      </Form>
    </div>
    </>
  );
}

export default App;
