import React, { useState } from "react";
import { Input, Row, Col, Card, Typography, Button, Form } from "antd";

// card
const { Title } = Typography;
function App() {
  const [input, setinput] = useState("");
  const [totaldata, settotaldata] = useState("");
  const [temp, settemp] = useState("");
  const [description, setdescription] = useState("");
  const [icon, seticon] = useState("");
  const getWeather = () => {
    let ApiKey= "a84b8d238f6967e52ec001101154352a"
    fetch(`https://api.openweathermap.org/data/2.5/forecast?q=${input}&appid=${ApiKey}&units=metric`)
    .then((response)=> response.json())
    .then((data)=>settotaldata(data));
  }
  console.log(totaldata);
  return (
    <div className="App">
      {
        totaldata
      }
      <Row justify="center">
        <Col span={24}>
          <Card style={{ height: 100 }}>
            <Title level={1}>Weather App</Title>
          </Card>
        </Col>
      </Row>
      <br />
      <br />
      <Form>
        <Row justify="center" style={{ marginTop: 100 }}>
          <Col span={6}>
            <Input placeholder="Enter the city name......" size="large" onChange={(e)=>setinput(e.target.value)}/>
          </Col>
        </Row>
        <br />
        <br />
        <Row justify="center">
          <Col>
            <Button type="primary" htmlType="submit" onClick={getWeather}>
              Search
            </Button>
          </Col>
        </Row>
      </Form>
    </div>

  );
}

export default App;
